import React from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { userActions,alertActions } from '../../_actions';

class RegisterPage extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            user: {
                email:'',
                fname: '',
                lname: '',
                password: ''
            },
            submitted: false
        };

        this.props.clearAlerts();
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);

    }

    handleChange(event) {
        const { name, value } = event.target;
        const { user } = this.state;
        this.setState({
            user: {
                ...user,
                [name]: value
            }
        });
    }

    handleSubmit(event) {
        event.preventDefault();
        this.setState({ submitted: true });
        const { user } = this.state;
        console.log(user);
        if (user.fname && user.lname && user.email && user.password && !this.props.emailtaken) {
            this.props.register(user,this.props.history);
        }
    }

    emailtaken(e) {
        clearTimeout(this.timer);
        this.timer = setTimeout(() => {
          this.props.getEmailValid(this.state.user.email)
        }, 1000);
    }

    render() {
        const { registering,emailtaken,loading  } = this.props;
        const { user, submitted } = this.state;
        return (
            <div className="col-md-6 col-md-offset-3">
                <h2>Register</h2>
                <form name="form" onSubmit={this.handleSubmit}>
                    <div className={'form-group' + (submitted && !user.email ? ' has-error' : '')}>
                        <label htmlFor="username">Email ID</label>
                        <input type="email" className="form-control" name="email" value={user.email} onChange={e => {
                            this.handleChange(e)
                            this.emailtaken(e) 
                        }} />
                        { emailtaken && <div className="help-block">Email id is already available.</div> }

                        {submitted && !user.email &&
                            <div className=" help-block">Email Id is required</div>
                        }
                    </div>
                    <div className={'form-group' + (submitted && !user.fname ? ' has-error' : '')}>
                        <label htmlFor="firstName">First Name</label>
                        <input type="text" className="form-control" name="fname" value={user.fname} onChange={this.handleChange} />
                        {submitted && !user.fname &&
                            <div className="help-block">First Name is required</div>
                        }
                    </div>
                    <div className={'form-group' + (submitted && !user.lname ? ' has-error' : '')}>
                        <label htmlFor="lastName">Last Name</label>
                        <input type="text" className="form-control" name="lname" value={user.lname} onChange={this.handleChange} />
                        {submitted && !user.lname &&
                            <div className="help-block">Last Name is required</div>
                        }
                    </div>

                    <div className={'form-group' + (submitted && !user.password ? ' has-error' : '')}>
                        <label htmlFor="password">Password</label>
                        <input type="password" className="form-control" name="password" value={user.password} onChange={this.handleChange} />
                        {submitted && !user.password &&
                            <div className="help-block">Password is required</div>
                        }
                    </div>
                    <div className="form-group">
                        <button className="btn btn-primary">Register</button>
                        {loading && 
                            <img alt='' src="data:image/gif;base64," />
                        }
                        <Link to="/login" className="btn btn-link">Cancel</Link>
                    </div>
                </form>
            </div>
        );
    }
}


function mapState(state) {
    const { loading }  = state
    const { registering,emailtaken } = state.registration;
    return { registering,emailtaken,loading };
}

const actionCreators = {
    register: userActions.register,
    clearAlerts: alertActions.clear,
    getEmailValid:userActions.getEmailValid
}

const connectedRegisterPage = connect(mapState, actionCreators)(RegisterPage);
export { connectedRegisterPage as RegisterPage };
