import React from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { userActions,alertActions } from '../../_actions';


class LoginPage extends React.Component {
    constructor(props) {
        super(props);

        // reset login status
        // this.props.logout();

        this.state = {
            email: '',
            password: '',
            submitted: false
        };

        if(props.users.loggingIn){
              props.history.push('/')
        }

        this.props.clearAlerts();
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(e) {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    handleSubmit(e) {
        e.preventDefault();

        this.setState({ submitted: true });
        const { email, password } = this.state;
        if (email && password) {
            let data = {
                email,
                password
            }
            this.props.login(data,this.props.history);
        }
    }

    render() {
        const { loggingIn } = this.props;
        const { email, password, submitted } = this.state;
        return (
            <div className="col-md-6 col-md-offset-3">
                <h2>Login</h2>
                <form name="form" onSubmit={this.handleSubmit}>
                    <div className={'form-group' + (submitted && !email ? ' has-error' : '')}>
                        <label htmlFor="email">Email Id</label>
                        <input type="email" className="form-control" name="email" value={email} onChange={this.handleChange} />
                        {submitted && !email &&
                            <div className="help-block">Email is required</div>
                        }
                    </div>
                    <div className={'form-group' + (submitted && !password ? ' has-error' : '')}>
                        <label htmlFor="password">Password</label>
                        <input type="password" className="form-control" name="password" value={password} onChange={this.handleChange} />

            <button class="btn btn-default reveal" type="button"><i class="glyphicon glyphicon-eye-closed"></i></button>

                        {submitted && !password &&
                            <div className="help-block">Password is required</div>
                        }
                    </div>
                    <div className="form-group">
                        <button className="btn btn-primary">Login</button>
                        {loggingIn &&
                            <img  alt='' src="data:image/gif;base64," />
                        }
                        <Link to="/register" className="btn btn-link">Register</Link>
                        <Link to="/forgotpass" className="btn btn-link">Forgot password</Link>
                    </div>
                </form>
            </div>
        );
    }
}


function mapState(state) {
    const { users  } = state;
    return { users };
}

const actionCreators = {
    login:userActions.login,
    clearAlerts: alertActions.clear,
};

const connectedLoginPage = connect(mapState, actionCreators)(LoginPage);
export { connectedLoginPage as LoginPage };
