import React, { Component } from 'react'
import { connect } from 'react-redux'
import './otp.css'

 class Otp extends Component {
     constructor(props){
         super(props)

         this.state = {
            ssn1: '',
            ssn2: '',
            ssn3: '',
            ssn4: '',
            ssn5: '',
            ssn6: '',
         }

         if(!props.email){
            props.history.push('/')
      }


      this.handleChange = this.handleChange.bind(this);
     }


    handleChange = e => {
        console.log("hitted..");
        const { maxLength, value, name } = e.target;
        const [fieldName, fieldIndex] = name.split("-");

        // Check if they hit the max character length
        if (value.length >= maxLength) {
          // Check if it's not the last input field
          if (parseInt(fieldIndex, 10) < 3) {
            // Get the next input field
            const nextSibling = document.querySelector(
              `input[name=ssn-${parseInt(fieldIndex, 10) + 1}]`
            );

            // If found, focus the next field
            if (nextSibling !== null) {
              nextSibling.focus();
            }
          }
        }

        setValue({
            ...value,
            [`ssn${fieldIndex}`]: value
          });
      }


    render() {
        return (
            <div>
             <form name="form" onSubmit={this.handleSubmit}>
             <h6>Please enter the one time password  to reset the password</h6>
             <div className="form-group">
             <div id="otp" className="inputs  "> 
                <input className="m-2 text-center form-control rounded oneline" type="text" id="ssn-1" maxLength="1"  onChange={this.handleChange}/> 
                <input className="m-2 text-center form-control rounded oneline" type="text" id="ssn-2" maxLength="1" onChange={this.handleChange}/>
                <input className="m-2 text-center form-control rounded oneline" type="text" id="ssn-3" maxLength="1" onChange={this.handleChange}/> 
                <input className="m-2 text-center form-control rounded oneline" type="text" id="ssn-4" maxLength="1" onChange={this.handleChange}/> 
                <input className="m-2 text-center form-control rounded oneline" type="text" id="ssn-5" maxLength="1" onChange={this.handleChange}/> 
                <input className="m-2 text-center form-control rounded oneline" type="text" id="ssn-6" maxLength="1" onChange={this.handleChange}/> 
             </div>
             </div>
                <div className="form-group">
                    <button className="btn btn-primary">Validate</button>
                    {/* {loading &&
                        <img src="data:image/gif;base64," />
                    } */}
                </div>
                <h6>Resend the code <a>here</a>?</h6>
                <h6>OTP is {} (Note* For thesting purpose.)</h6>
            </form>
            </div>
        )
    }
}



function mapState(state) {
    const { email } = state.forgotpass;
    return {email };
}

const actionCreators = {
    // getOTP:userActions.getOTP
};

const otpApp = connect(mapState, actionCreators)(Otp);
export { otpApp as Otp };
