import React, { Component } from 'react'
import { connect } from 'react-redux'
import './otp.css'

 class Otp extends Component {
     constructor(props){
         super(props)
         if(!props.email){
            props.history.push('/')
      }
     }


    render() {
        return (
            <div>
             <form name="form" onSubmit={this.handleSubmit}>
             <h6>Please enter the one time password  to reset the password</h6>
             <div className="form-group">
             <div id="otp" className="inputs  "> 
                <input className="m-2 text-center form-control rounded oneline" type="text" id="first" maxLength="1" /> 
                <input className="m-2 text-center form-control rounded oneline" type="text" id="second" maxLength="1" />
                <input className="m-2 text-center form-control rounded oneline" type="text" id="third" maxLength="1" /> 
                <input className="m-2 text-center form-control rounded oneline" type="text" id="fourth" maxLength="1" /> 
                <input className="m-2 text-center form-control rounded oneline" type="text" id="fifth" maxLength="1" /> 
                <input className="m-2 text-center form-control rounded oneline" type="text" id="sixth" maxLength="1" /> 
             </div>
             </div>
                <div className="form-group">
                    <button className="btn btn-primary">Validate</button>
                    {/* {loading &&
                        <img src="data:image/gif;base64," />
                    } */}
                </div>
                <h6>Resend the code <a>here</a>?</h6>
                <h6>OTP is {} (Note* For thesting purpose.)</h6>
            </form>
            </div>
        )
    }
}



function mapState(state) {
    const { email } = state.forgotpass;
    return {email };
}

const actionCreators = {
    // getOTP:userActions.getOTP
};

const otpApp = connect(mapState, actionCreators)(Otp);
export { otpApp as Otp };
