import React, { Component } from 'react'
import { connect } from 'react-redux';


class NewPassword extends Component {
    constructor(props) {
        super(props);

        this.state = {
            newPassword: '',
            submitted: false
        };

        if(!props.email && !props.token){
            props.history.push('/')
         }

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }


    handleChange(e) {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    handleSubmit(e) {
        e.preventDefault();

        this.setState({ submitted: true });
        const { newPassword, currPassword } = this.state;
        // if (newPassword && currPassword) {
        //     let data = {
        //         newPassword,
        //         currPassword
        //     }
        //     this.props.changePass(data,this.props.history);
        // }
    }


    render() {

        const { newPassword, currPassword, submitted } = this.state;
        return (
     <div>
        <div className="col-md-6 col-md-offset-3">
            <h2>Reset Password</h2>
              <form name="form" onSubmit={this.handleSubmit}>
                <div className={'form-group' + (submitted && !currPassword ? ' has-error' : '')}>
                    <label htmlFor="password">New Password</label>
                    <input type="password" className="form-control" name="currPassword" value={currPassword} onChange={this.handleChange} />
                    {submitted && !currPassword &&
                        <div className="help-block">Current password is required</div>
                    }
                </div>
                <div className="form-group">
                    <button className="btn btn-primary">Change Password</button>
                    {/* {loading && 
                        <img src="data:image/gif;base64," />
                    } */}
                </div>
            </form>
            </div>
        </div>
        )
    }
}


function mapState(state) {
    const { email,token } = state.forgotpass;
    return {email,token };
}

const actionCreators = {
    otpVerification:userActions.otpVerification
};


const newPasswordApp = connect(mapState, actionCreators)(NewPassword);
export { newPasswordApp as NewPassword };
