import { alertActions } from '../_actions';

export * from './alert.constants';
export * from './user.constants';
export * from './forgotPass.constant'

export const clearError = (dispatch) => {
    setTimeout(() => {
        dispatch(alertActions.clear()); 
    }, 5000);
}
