import React, { Component } from 'react'

export class forgotpassword extends Component {
    constructor(props) {
        super(props);

        this.state = {
            newPassword: '',
            currPassword: '',
            submitted: false
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }


    handleChange(e) {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    handleSubmit(e) {
        e.preventDefault();

        this.setState({ submitted: true });
        const { newPassword, currPassword } = this.state;
        if (newPassword && currPassword) {
            let data = {
                newPassword,
                currPassword
            }
            this.props.login(data);
        }
    }

    render() {
        return (
            <div className="col-md-6 col-md-offset-3">
            <h2>Change Password</h2>
            <form name="form" onSubmit={this.handleSubmit}>
                <div className={'form-group' + (submitted && !currPassword ? ' has-error' : '')}>
                    <label htmlFor="email">Email Id</label>
                    <input type="email" className="form-control" name="currPassword" value={currPassword} onChange={this.handleChange} />
                    {submitted && !currPassword &&
                        <div className="help-block">Current password is required</div>
                    }
                </div>
                <div className={'form-group' + (submitted && !newPassword ? ' has-error' : '')}>
                    <label htmlFor="password">Password</label>
                    <input type="password" className="form-control" name="newPassword" value={newPassword} onChange={this.handleChange} />
                    {submitted && !newPassword &&
                        <div className="help-block">New password is required</div>
                    }
                </div>
                <div className="form-group">
                    <button className="btn btn-primary">Change Password</button>
                    {loggingIn &&
                        <img src="data:image/gif;base64," />
                    }
                </div>
            </form>
        </div>
        )
    }
}

export default forgotpassword
