import React, { Component } from 'react'
import { connect } from 'react-redux';
import { userActions } from '../../_actions';

class ChangePassword extends Component {
    constructor(props) {
        super(props);

        this.state = {
            newPassword: '',
            currPassword: '',
            submitted: false,
            error:{}
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }


    handleChange(e) {
        const { name, value } = e.target;
        this.validation(e);
        this.setState({ [name]: value });
    }

    handleSubmit(e) {
        e.preventDefault();

        this.setState({ submitted: true });
        const { newPassword, currPassword } = this.state;
        if (newPassword && currPassword) {
            let data = {
                newPassword,
                currPassword
            }
            this.props.changePass(data,this.props.history);
        }
    }




    render() {
        let { loading }  = this.props;
        const { newPassword, currPassword, submitted } = this.state;
        return (
            <div className="col-md-6 col-md-offset-3">
            <h2>Change Password</h2>
            <form name="form" onSubmit={this.handleSubmit}>
                <div className={'form-group' + (submitted && !currPassword ? ' has-error' : '')}>
                    <label htmlFor="password">Current Password</label>
                    <input type="password" className="form-control" name="currPassword" value={currPassword} onChange={this.handleChange} />
                    {submitted && !currPassword &&
                        <div className="help-block">Current password is required</div>
                    }
                    {currPassword && currPassword.length < 6 &&
                         <div className="help-block">Minimum 6 digit required</div>
                    }
                </div>
                <div className={'form-group' + (submitted && !newPassword ? ' has-error' : '')}>
                    <label htmlFor="password">New Password</label>
                    <input type="password" className="form-control" name="newPassword" value={newPassword} onChange={this.handleChange} />
                </div>
                <div className="form-group">
                    <button className="btn btn-primary">Change Password</button>
                    {loading && 
                        <img  alt='' src="data:image/gif;base64," />
                    }
                </div>
            </form>
        </div>
        )
    }
}

function mapState(state) {
    const { loading  } = state;
    return { loading  };
}

const actionCreators = {
    changePass:userActions.changePass
};

const connectedChangPasswordPage = connect(mapState, actionCreators)(ChangePassword);

export { connectedChangPasswordPage as ChangePassword };
