import React from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { userActions,alertActions, validEmail,minMaxLength } from '../../_actions';


class LoginPage extends React.Component {
    constructor(props) {
        super(props);

        // reset login status
        // this.props.logout();

        this.state = {
            email: '',
            password: '',
            submitted: false,
            error:{},
            FormValidate:true,
        };

        if(props.users.loggingIn){
              props.history.push('/')
        }

        // this.props.clearAlerts();
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(e) {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    handleSubmit(e) {
        e.preventDefault();
        this.setState({ submitted: true });
        const { email, password,error } = this.state;
        if (email && password && !error.email && !error.password) {
            let data = {
                email,
                password
            }
            this.props.login(data,this.props.history);
        }
    }

    validation(e){
        const { name, value } = e.target;
        if(name === 'email'){
            let emailval = validEmail(value)
                this.setState({
                    error:!emailval? {...this.state.error,email:'Email is Invalid'}:''
                })
        }

        if(name === 'password'){
         let passVal = minMaxLength(value,5,10);
            this.setState({
                error:passVal ? {...this.state.error,password:'Password should be  minimum 5 to maximum 10 digits'} :''
            })
        }
    }

    render() {
        const { loading } = this.props;
        const { email, password, submitted, error} = this.state;
        return (
            <div className="col-md-6 col-md-offset-3">
                <h2>Login</h2>
                <form name="form" onSubmit={this.handleSubmit}>
                    <div className={'form-group' + (submitted && !email ? ' has-error' : '')}>
                        <label htmlFor="email">Email Id</label>
                        <input type="text" className="form-control" name="email" value={email} onChange={this.handleChange} onBlur={e => this.validation(e)} />
                        {submitted && !email &&
                            <div className="help-block">Email is required</div>
                        }
                        {error.email &&  <div className="help-block">{error.email}</div> }
                    </div>
                    <div className={'form-group' + (submitted && !password ? ' has-error' : '')}>
                        <label htmlFor="password">Password</label>
                        <input type="password" className="form-control" name="password" value={password} onChange={this.handleChange} onBlur={e => this.validation(e)} />
                        {submitted && !password &&
                            <div className="help-block">Password is required</div>
                        }
                         {error.password &&  <div className=" help-block" >{error.password}</div> }
                    </div>
                    <div className="form-group">
                        <button className="btn btn-primary">Login</button>
                        {loading &&
                            <img  alt='' src="data:image/gif;base64," />
                        }
                        <Link to="/register" className="btn btn-link">Register</Link>
                        <Link to="/forgotpass" className="btn btn-link">Forgot password</Link>
                    </div>
                </form>
            </div>
        );
    }
}


function mapState(state) {
    const { users ,loading } = state;
    return { users ,loading};
}

const actionCreators = {
    login:userActions.login,
    clearAlerts: alertActions.clear,
    validEmail:validEmail,
    minMaxLength:minMaxLength
};

const connectedLoginPage = connect(mapState, actionCreators)(LoginPage);
export { connectedLoginPage as LoginPage };
