import React, { Component } from 'react'
import { connect } from 'react-redux';
import { userActions } from '../../_actions';



class NewPassword extends Component {
    constructor(props) {
        super(props);

        this.state = {
            password: '',
            submitted: false
        };

        if(!props.email && !props.token){
            props.history.push('/')
         }

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }


    handleChange(e) {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    handleSubmit(e) {
        e.preventDefault();

        this.setState({ submitted: true });
        const { password } = this.state;
        if (password) {
            let data = {
                password,
                token:this.props.token
            }
            this.props.resetPassword(data,this.props.history);
        }
    }


    render() {
        let  { loading } = this.props;
        const { password, submitted } = this.state;
        return (
     <div>
        <div className="col-md-6 col-md-offset-3">
            <h2>Reset Password</h2>
              <form name="form" onSubmit={this.handleSubmit}>
                <div className={'form-group' + (submitted && !password ? ' has-error' : '')}>
                    <label htmlFor="password">New Password</label>
                    <input type="password" className="form-control" name="password" value={password} onChange={this.handleChange} />
                    {submitted && !password &&
                        <div className="help-block">New password is required</div>
                    }
                </div>
                <div className="form-group">
                    <button className="btn btn-primary">Change Password</button>
                    {loading && 
                        <img alt='' src="data:image/gif;base64," />
                    }
                </div>
            </form>
            </div>
        </div>
        )
    }
}


function mapState(state) {
    let { loading } = state;
    const { email,token } = state.forgotpass;
    return {email,token,loading };
}

const actionCreators = {
    resetPassword:userActions.resetPassword
};


const newPasswordApp = connect(mapState, actionCreators)(NewPassword);
export { newPasswordApp as NewPassword };
